package week1.day1;

public class PositiveOrNegative {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=82;
		
		if (a>0) {
			
			System.out.println("The given number is positive " +a);
		}
		
		else if (a<0) {
			
			System.out.println("The given number is Negative" +a);
			
		}
		
	}

}
